INSERT INTO `question` (`id`, `type`, `grade`, `estimate`, `difficulty`, `category`, `content`, `answer`) VALUES
(27, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Giải phương trình, bất phương trình sau:\na, <m>\\left|{\\frac{2x-1}{x+1}}\\right| >2</m>\nb, <m>x^2+\\sqrt{x^2+2x+8}=12-2x</m>', ''),
(4, 0, 12, 20, 0, 'Khảo sát hàm số', 'Cho hàm số y = <m> \\frac{-x+1}{2x-1}</m>.\n1. Khảo sát sự biến thiên và vẽ đồ thị hàm số đã cho.\n2. Chứng minh rằng với mọi m đường thẳng y = x + m luôn cắt đồ thị tại hai điểm phân biệt A và B. Gọi <m> k_1, k_2</m> lần lượt là hệ số góc của các tiếp tuyến với (C) tại A và B. Tìm m để tổng <m>k_1 + k_2 </m> đạt giá trị lớn nhất', ''),
(5, 0, 12, 20, 0, 'Khảo sát hàm số', 'Cho hàm số y = <m>3x-x^3</m> có đồ thị là (C).\n1. Khảo sát sự biến thiên và vẽ đồ thị hàm số đã cho.\n2. Tìm trên đường thẳng y = 2 những điểm mà từ đó kẻ được ba tiếp tuyến với (C).', ''),
(214, 0, 11, 10, 0, 'Tổ hợp- Xác suất', 'Giải hệ phương trình sau:\n<m> \\left\\{ \\begin{array}{l} 2A^y_x+5C^y_x=90\\\\5A^y_x-2C^y_x=80 \\end{array} \\right</m>.\n', ''),
(6, 0, 11, 15, 0, 'Phương trình lượng giác', 'Giải phương trình <m>\\frac{(1 + sinx + cos2x)sin(x + \\frac{\\pi}{4})}{1 + tanx} = \\frac{1}{\\sqrt{2}}cosx </m>', 'x=<m>\\frac{\\pi}{2}+2k\\pi} </m>\nhoặc x=<m>\\frac{\\pi}{6}+2k\\pi} </m>\nhoặc x=<m>\\frac{\\pi}{6}+2k\\pi} </m>\n\n\n'),
(7, 1, 10, 5, 0, 'Cung và góc lượng giác', 'Trong các hệ thức sau, hệ thức nào đúng\nA. <m>1 + tan^2a = 1/{sin^2a}  (sina <> 0)</m>\nB. <m>sin4a = 4sinacosa</m>\nC. <m>sin^2{2a} + cos^2{2a} = 1</m>\nD. <m>1 + cot^2{a} = 1/{cos^2{a}} (cosa <> 0)</m>', 'C. <m>sin^2{2a} + cos^2{2a} = 1</m>'),
(8, 1, 10, 5, 0, 'Cung và góc lượng giác', 'Cho sina = 1/3, với <m>90^o < a < 180^o</m>. Giá trị của cosa là:\nA. <m>-2\\frac{\\sqrt{2}}{3}</m>\nB. <m>\\frac{8}{9}</m>\nC. <m>\\frac{\\pm{-2{\\sqrt{2}}}}{3}</m>\nD. <m>\\frac{\\sqrt{2}}{3}</m>', 'A. <m>-2\\frac{\\sqrt{2}}{3}</m>'),
(9, 1, 10, 5, 0, 'Cung và góc lượng giác', 'Cho tam giác ABC, tan(3A + B + C).cot(B + C - A) có giá trị bằng:\nA. 2\nB. -1\nC. -4\nD. 1', 'D. 1'),
(10, 1, 10, 5, 0, 'Cung và góc lượng giác', 'Cho 0 < a, b < <m>\\frac{\\pi}{2}</m>, tana = 1/2, tanb = 1/3. Góc a + b có giá trị bằng:\nA. <m>3\\frac{\\pi}{4}</m>\nB. <m>1</m>\nC. <m>\\frac{\\pi}{4}</m>\nD. <m>\\frac{5\\pi}{4}</m>', 'C. <m>\\frac{\\pi}{4}</m>'),
(11, 1, 10, 5, 0, 'Cung và góc lượng giác', 'Giá trị biểu thức A = <m>sin^2{45^o} + cot^2{60^o} - 1/{cos^2{135^o}}</m> bằng:\nA. 7/6\nB. -6/7\nC. -7/6\nD. 6/7', 'C. -7/6'),
(12, 0, 10, 10, 0, 'Cung và góc lượng giác', 'Cho cosa = 3/5 với <m>\\frac{\\pi}{4} < a < \\frac{\\pi}{2}</m>. Tính cos2a, sin2a', ''),
(13, 0, 10, 10, 0, 'Cung và góc lượng giác', 'Chứng minh các đẳng thức\na) <m>cos^3{a}sin{a} - sin^3{a}cos{a} = {1/4}{sin4a}</m>\nb) <m>sin^2(\\frac{\\pi}{8} + a) - sin^2(\\frac{\\pi}{8} - a) = \\frac{sin2a}{\\sqrt{2}}</m>', ''),
(14, 0, 10, 10, 0, 'Cung và góc lượng giác', 'Chứng minh rằng tam giác ABC cân nếu <m>{sinB}/{sinC} = 2cosA</m>', ''),
(15, 0, 10, 10, 0, 'Cung và góc lượng giác', 'Chứng minh biểu thức sau không phụ thuộc vào x, y:\nA = <m>\\frac{sin^x}{cos^2y} + tan^2{y}cos^2{x} - sin^2{x} - tan^2{y}</m>', ''),
(16, 1, 10, 5, 2, 'Cung và góc lượng giác', 'Cho góc x thoả mãn <m>90^o < x < 180^o</m>. Mệnh đề nào sau đây là đúng?\nA. sinx < 0\nB. cosx < 0\nC. tanx > 0\nD. cotx > 0', 'B. cosx < 0'),
(17, 1, 10, 5, 0, 'Cung và góc lượng giác', '''Tính giá trị biểu thức P = <m>cos23^o + cos215^o + cos275^o + cos287^o</m>\nA. P = 0\nB. P = 1\nC. P = 2\nD. P = 4', 'A. P = 0'),
(217, 0, 12, 5, 0, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m> \\int^{0}_{\\pi/2}{(cos^2 x+1)dx}</m>', ''),
(18, 0, 10, 10, 0, 'Cung và góc lượng giác', 'Rút gọn biểu thức sau <m>A = \\frac{sin(a+b)sin(a-b)}{cos^2{a}cos^2{b}}</m>', ''),
(19, 0, 10, 10, 1, 'Cung và góc lượng giác', 'Chứng minh các đẳng thức sau:\na, <m>\\frac{1 + sin2x}{sin^2{x} - cos^2{x}} = \\frac{tanx + 1}{tanx - 1}</m>\nb, <m>\\frac{1 - cosx}{sinx} = \\frac{sinx}{1 + cosx}</m> (với <m>x <> k{\\pi}, k \\in Z</m>)', ''),
(20, 0, 10, 10, 0, 'Cung và góc lượng giác', 'Cho A = <m>sin(\\alpha + \\frac{\\pi}{4}) + sin(\\alpha - \\frac{\\pi}{4})</m>\na, Chứng minh rằng A = <m>\\sqrt{2}sin{\\alpha}, forall \\alpha \\in R</m>\nb, Tìm <m>\\alpha \\in (\\pi/2 ; \\pi)</m> để A = <m>\\sqrt{2}/2</m>', ''),
(21, 0, 10, 10, 0, 'Cung và góc lượng giác', 'Biết <m>tan{a/2} = \\sqrt{2}/3</m>, tính cosa và sin2a', ''),
(22, 0, 10, 10, 0, 'Cung và góc lượng giác', 'Tính giá trị biểu thức A = <m>(cos110^o + cos10^o)^2 - cos^2{50^o}</m>', ''),
(23, 1, 10, 5, 0, 'Cung và góc lượng giác', 'Biết sinx = 1/5 và <m>\\pi/2 < x < \\pi</m>. Giá trị của cosx là:\nA. <m>4/5</m>\nB. <m>\\sqrt{24/25}</m>\nC. <m>-2{\\sqrt{6}}/5</m>\nD. <m>-4/5</m>', 'C. <m>-2{\\sqrt{6}}/5</m>'),
(24, 1, 10, 5, 0, 'Cung và góc lượng giác', 'Biết <m>\\pi/4 < \\alpha < \\pi/2</m>, hãy chọn câu đúng:\nA. <m>cot{\\alpha} < 0</m>\nB. <m>tan{2{\\alpha}} < 0</m>\nC. <m>cos{3{\\alpha}} > 0</m>\nD. <m>sin{4{\\alpha}} > 0</m>', 'B. <m>tan{2{\\alpha}} < 0</m>'),
(342, 0, 11, 10, 0, 'Hình học không gian 11', 'Gọi I, J lần lượt là hai điểm  nằm trong  mặt phẳng (ABC); (ABD) của tứ diện ABCD. M là điểm tùy ý trên CD. Tìm giao điểm IJ và mặt phẳng (AMB).', 'Kẻ AI cắt BC tại K, AJ cắt CD tại H.Nối KH cắt BM tại O. Kẻ IJ cắt AO tại G. khi đó G là giao của IJ với (BAM).'),
(216, 0, 12, 5, 0, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m> \\int^{1}_{2}{(3x^2-2x+1)dx}</m>', ''),
(26, 1, 10, 5, 0, 'Cung và góc lượng giác', 'Điều kiện để đẳng thức  <m>tan{\\alpha}cot{\\alpha} = 1</m> không có nghĩa là:\r\nA. <m>\\alpha = \\pi/2 + k2{\\pi}, k \\in Z</m>\r\nB. <m>\\alpha = -\\pi/2 + k2{\\pi}, k \\in Z</m>\r\nC. <m>\\alpha = \\pi/2 + k{\\pi}, k \\in Z</m>\r\nD. <m>\\alpha = k{\\pi}/2, k \\in Z</m>', 'D. <m>\\alpha = k{\\pi}/2, k \\in Z</m>'),
(28, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Tìm m để:\n<m>(m^2-1)x^2 + 2(m+1)x + 3 \\geq 0, \\forall x \\in R</m>', ''),
(29, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Tìm giá trị lớn nhất của biểu thức A = (3 - x)(4 - y)(2x + 3y) với 0 < x < 3; 0 < y < 4', ''),
(30, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Hệ bất phương trình\r\n<m>\\left\\{\\begin{array}{l}x^2 - 1 \\leq 0\\\\x - m > 0\\end{array}\\right</m>\r\ncó nghiệm khi:\r\nA. m < 1\r\nB. m<m>\\geq</m> 1\r\nC. m = 1\r\nD. m<m>\\not=</m> 1 ', 'B. m<m>\\geq</m> 1'),
(31, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Hệ bất phương trình \r\n<m>\\left\\{\\begin{array}{l}x^2 - 5x + 6 \\leq 0\\\\ax - 4 < 0\\end{array}\\right</m>\r\nvô nghiệm với:\r\nA. <m> a \\geq -\\frac{4}{3}</m>\r\nB. <m> a < -\\frac{4}{3}</m>\r\nC. a > -4\r\nD. <m> -\\frac{4}{3} \\leq a < 0 </m>', ''),
(32, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Tập nghiệm của phương trình <m>\\sqrt{x^2-3x+2} = x-3</m> là\nA. {7/3}\nB. <m>\\Phi</m>\nC. {3/7}\nD. {2}', ''),
(33, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Phương trình <m>(m^2+6m-16)x^2 + (m+1)x - 5 = 0</m> có hai nghiệm trái dấu với\nA. m < -8 và m > -2\nB. -8 < m < -2\nC. <m>m \\leq -8</m>\nD. m > -2', ''),
(34, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Cho tam thức bậc hai <m>f(x) = x^2 + (1+\\sqrt{3})x - 8 + 5\\sqrt{3}</m>. Chọn phương án đúng.\nA. f(x) > 0, <m>\\forall x \\in R</m>\nB. f(x) > 0, <m>\\forall x \\in [1; 2]</m>\nC. f(x) < 0, <m>\\forall x \\in (1; 2)</m>\nD. f(x) < 0, <m>\\forall x \\in R</m>', ''),
(35, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Tập xác định của hàm số <m>y = \\sqrt{-x^2+1} + \\frac{1}{x^2 - x} + 5x</m> là\nA. [-1; 1) \\ {0}\nB. R \\ {-1; 0; 1}\nC. [-1; 1]\nD. R', ''),
(36, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Bất phương trình <m>\\frac{x^2-4}{x^2-3x+2} \\geq 0</m> có tập nghiệm là:\nA. <m>(-\\infty; -2] \\cup (1, +\\infty)</m>\nB. R \\ (-2; 1]\nC. <m>(-\\infty; -2] \\cup (1; 2) \\cup (2; +\\infty)</m>\nD. <m>(-\\infty; -2] \\cup [1, +\\infty)</m>', ''),
(37, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Bất phương trình <m>(m^2 - 1)x + 1 > m</m> vô nghiệm khi\nA. m = -1\nB. m = <m>\\pm 1</m>\nC. m = 1\nD. m = 0', ''),
(38, 0, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Giải bất phương trình\n<m>\\frac{2+7x-15x^2}{3x^2-7x+2} \\geq 0</m>', ''),
(39, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Cho bất phương trình <m>(m - 2)x^2 + 2(2m-3)x + 5m-6 > 0</m> với m là tham số. Tìm m để bất phương trình trên vô nghiệm.', ''),
(40, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Giải bất phương trình <m>(2-7x+3x^2)\\sqrt{3-5x-2x^2} \\geq 0</m>', ''),
(41, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Nghiệm của bất phương trình <m>x^2 - 9 \\leq 0</m> là\nA. <m>x = \\pm3</m>\nB. <m>x \\leq \\pm3</m>\nC. <m>x \\leq -3</m> và <m>x \\geq 3</m>\nD. <m> -3 \\leq x \\leq 3</m>', ''),
(42, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Tập nghiệm của hệ bất phương trình\n<m>\\left\\{\\begin{array}{l} x^2-4x+3>0 \\\\  (x+2)(x-5) < 0 \\end{array}\\right</m>\nlà\nA. (1; 3)\nB. <m>(-2; 1) \\cup (3; 5)</m>\nC. (-2; 5)\nD. (3; 5)', ''),
(43, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Tập các giá trị của m để phương trình \n<m>x^2 -4(m+1)x + m(m-5)=0</m> với m là tham số có nghiệm là:\nA. <m>\\left( -4; -\\frac{1}{3} \\right)</m>\nB. <m>\\left( -\\infty; -4 \\right) \\cup \\left( -\\frac{1}{3}; +\\infty \\right)</m>\nC. <m>\\left( -\\infty; -4 \\right] \\cup \\left[-\\frac{1}{3}; +\\infty \\right)</m>\nD. <m>\\left[ -4; -\\frac{1}{3} \\right]</m>', ''),
(44, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Với giá trị nào của m thì tập nghiệm của bất phương trình sau là R?\n<m>x^2 - mx + m+ 3>0</m>\nA. m < -2 hoặc m > 6\nB. -2 < m < 6\nC. m < -6 hoặc m > -2\nD. -6 < m < -2', ''),
(45, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Giải các bất phương trình sau:\r\na, <m>\\left| x^2-5x-4 \\right| \\leq x^2+6x+5</m>\r\nb, <m>4x^2 + 4x - |2x+1| \\geq 5</m>\r\n', ''),
(391, 0, 10, 10, 1, 'Bất đẳng thức - bất phương trình', 'Giải các bất phương trình sau:\na, <m>\\frac{2x-4}{\\sqrt{x^2-3x-10}} > 1</m>\nb, <m>\\sqrt{x^2+6x+8} \\leq 2x+3</m>', ''),
(46, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Giải hệ phương trình:\n<m>\\left\\{\\begin{array}{l}  x^2 - 9 < 0\\\\ (x-1)(3x^2+7x+4) \\geq 0 \\end{array}\\right</m>', ''),
(47, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Tìm m để bất phương trình sau có tập nghiệm là R:\n<m>(m-1)x^2 - 2(m+1)x + 3(m-2) > 0</m>', ''),
(48, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Tập nghiệm của bất phương trình <m>x^2 - 2x - 3 < 0</m> là\nA. (-1, 3)\nB. <m>( -\\infty; -1) \\cup (3; +\\infty)</m>\nC. (-3, 1)\nD. <m>( -\\infty; -3) \\cup (1; +\\infty)</m>', ''),
(49, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Tập nghiệm của bất phương trình <m>(x+3)(x-1)^2 \\leq 0</m> là\nA. <m>(-\\infty; -3]</m>\nB. [-3; 1]\nC. <m>(-\\infty; -3] \\cup {1}</m>\nD. <m>(-\\infty; -3) \\cup {1}</m>', ''),
(50, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Tập nghiệm của hệ bất phương trình \n<m>\\left\\{\\begin{array}{l}  \\frac{1}{3x} < 1 \\\\ 4x^2 - 5x + 1\\geq 0 \\end{array}\\right</m> là:\nA. <m>[1; +\\infty)</m>\nB. <m>(-\\infty; 0) \\cup [1; +\\infty)</m>\nC. <m>[\\frac{1}{4}; +\\infty)</m>\nD. <m>[\\frac{1}{4}; \\frac{1}{3}]</m>', ''),
(51, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Tập nghiệm của bất phương trình <m>(x-2)^2(x-7) \\geq 0</m> là:\nA. <m>[7; +\\infty)</m>\nB. <m>(-\\infty; 2) \\cup [7; +\\infty)</m>\nC. <m>(7; +\\infty) \\cup {2}</m>\nC. <m>[7; +\\infty) \\cup {2}</m>', ''),
(52, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Tam thức bậc hai <m>f(x) = x^2+(1-\\sqrt{3})x+1</m>\nA. Dương với mọi x\nB. Âm với mọi x\nC. Âm với mọi x thuộc <m>(-\\infty, \\sqrt{3})</m>\nD. Không câu nào đúng', ''),
(53, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Tam thức bậc hai <m>f(x)=(1-\\sqrt{2})x^2+(5-4\\sqrt{2})x-3\\sqrt{2}+6</m>\nA. Dương với mọi x\nB. Dương với mọi x thuộc <m>(-3, \\sqrt{2})</m>\nC. Dương với mọi x thuộc <m>(-4, \\sqrt{2})</m>\nD. Âm với mọi x', ''),
(54, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Tập xác định hàm số <m>f(x)=\\sqrt{(2-\\sqrt{5})x^2+(15-7\\sqrt{5})x+25-10\\sqrt{5}}</m> là\nA. R\nB. <m>(-\\infty, 1)</m>\nC. [-5; 1]\nD. <m>[-5; \\sqrt{5}]</m>', ''),
(55, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Tập nghiệm của bất phương trình <m>(3-2\\sqrt{2})x^2-2(3\\sqrt{2}-4}x+6(2\\sqrt{2}-3) \\leq 0</m> là\nA. <m>[-\\sqrt{2}, 3\\sqrt{2}]</m>\nB. <m>(-\\infty; 1]</m>\nC. <m>[-1; +\\infty;]</m>\nD. <m>[-1, 3\\sqrt{2}]</m>', ''),
(56, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Tập nghiệm của bất phương trình\n<m>\\frac{(x-1)(x^3-1)}{x^2+(1+2\\sqrt{2})x+2+\\sqrt{2}} \\leq 0</m> \nlà:\nA. <m>(-1-\\sqrt{2}, -\\sqrt{2})</m>\nB. <m>(-1-\\sqrt{2}; 1]</m>\nC. <m>(-1-\\sqrt{2}, -\\sqrt{2}) \\cup {1}</m>\nD. <m>[1; +\\infty)</m>', ''),
(57, 1, 10, 5, 1, 'Bất đẳng thức - bất phương trình', 'Tập nghiệm của bất phương trình <m>\\sqrt{2(x-2)(x-5)} > x-3</m> là:\nA. [-100; 2]\nB. <m>(-\\infty; 1]</m>\nC. <m>(-\\infty; 2] \\cup [6; +\\infty)</m>\nC. <m>(-\\infty; 2] \\cup [4+ \\sqrt{5}; +\\infty)</m>', ''),
(58, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Chứng minh bất đẳng thức\n<m>a + b + c \\geq \\sqrt{ab} + \\sqrt{bc} + \\sqrt{ca}</m> với <m>a, b, c \\geq 0</m>', ''),
(59, 0, 10, 15, 0, 'Bất đẳng thức - bất phương trình', 'Cho <m>f(x) = (m+1)x^2-2(m+1)x-1</m>\na, Tìm m để phương trình f(x) = 0 có nghiệm\nb, Tìm m để <m>f(x) \\geq 0, \\forall x \\in R</m>', ''),
(60, 0, 10, 15, 0, 'Bất đẳng thức - bất phương trình', 'Giải bất phương trình sau\r\n<m>\\frac{2}{2x+1} > \\frac{5}{x-1}</m>', ''),
(61, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Chứng minh rằng \r\n<m>a^4+b^4 \\geq a^3b + ab^3, \\forall a, b \\in R</m>', ''),
(62, 0, 10, 20, 1, 'Bất đẳng thức - bất phương trình', 'Cho phương trình <m>-x^2 + (m+2)x - 4=0</m>. Tìm cá giá trị của tham số m để phương trình có:\r\na, Hai nghiệm phân biệt\r\nb, Hai nghiệm dương phân biệt.', ''),
(63, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', '<m>M_0 (0; -3)</m> thuộc miền nghiệm của hệ bất phương trình\nA. <m>\\left\\{ \\begin{array}{l} 2x-y \\leq 3 \\\\ 2x+5y \\leq 12x+8 \\end{array}</m>\nB. <m>\\left\\{ \\begin{array}{l} 2x-y>3 \\\\ 2x+5y \\leq 12x+8 \\end{array}</m>\nC. <m>\\left\\{ \\begin{array}{l} 2x-y \\leq 3 \\\\ 2x+5y \\geq 12x+8 \\end{array}</m>\nD. <m>\\left\\{ \\begin{array}{l} 2x-y \\leq 3 \\\\ 2x+5y \\leq 12x+8 \\end{array}</m>', 'C. <m>\\left\\{ \\begin{array}{l} 2x-y \\leq 3 \\\\ 2x+5y \\geq 12x+8 \\end{array}</m>'),
(64, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Phương trình <m>x^2+(2m-3)x+m^2-6 = 0</m> vô nghiệm khi\nA. m = 33/12\nB. m < 33/12\nC. m >= 33/12\nD. m > 33/12', ''),
(65, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Giải bất phương trình sau\n<m>\\left| \\frac{3x+1}{x-3} \\right| < 3</m>', ''),
(66, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Với giá trị nào của tham số m, hàm số <m>y = \\sqrt{x^2-mx+m}</m> có tập xác định là <m>(-\\infty; +\\infty)</m>', ''),
(67, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Cho phương trình <m>mx^2-2(m-1)x + 4m-1 = 0</m>. Tìm các giá trị của m để\na, Phương trình trên có nghiệm\nb, Phương trình trên có hai nghiệm dương phân biệt\n', ''),
(68, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Cho a, b, c là những số dương. Chứng minh rằng\n<m>(a+b)(b+c)(c+a) \\geq 8abc</m>', ''),
(69, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Hệ bất phương trình <m>\\left\\{ \\begin{array}{l} (x+3)(4-x)>0 \\\\ x<m-1 \\end{array}</m> có nghiệm khi\nA. m < 5\nB. m > -2\nC. m = 5\nD. m > 5', ''),
(70, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Hệ bất phương trình <m>\\left\\{ \\begin{array}{l} 2-x>0 \\\\ 2x+1>x-2 \\end{array}</m> có tập nghiệm là:\nA. <m>(-\\infty; -3)</m>\nB. (-3; 2)\nC. <m>(2; +\\infty)</m>\nD. <m>(-3; +\\infty)</m>', ''),
(71, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Bất phương trình <m>\\frac{2-x}{2x+1} \\geq 0</m> có tập nghiệm là\nA. (-1/2; 2)\nB. [-1/2; 2]\nC. [-1/2; 2)\nD. (-1/2; 2]', ''),
(72, 1, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Bất phương trình mx > 3 vô nghiệm khi:\nA. m = 0\nB. m > 0\nC. m < 0\nD. m <> 0', 'A. m = 0'),
(73, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Giải phương trình <m>\\sqrt{2x^2+4x-1} = x+1</m>', ''),
(74, 0, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Xét dấu tam thức bậc hai <m>f(x) = x^2 + 4x - 1</m>', ''),
(75, 0, 10, 5, 0, 'Bất đẳng thức - bất phương trình', 'Giải hệ bất phương trình sau:\n<m>\\left\\{ \\begin{array}{l} 5x-2>4x+5 \\\\ 5x-4 < x+2 \\end{array}</m>', ''),
(76, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Tìm giá trị lớn nhất và nhỏ nhất của hàm số\nf(x) = (x+3)(5-x) với <m>-3 \\leq x \\leq 5</m>', ''),
(77, 0, 10, 10, 1, 'Bất đẳng thức - bất phương trình', 'Tìm m để hàm số sau xác định với mọi x\n<m>y = \\frac{1}{\\sqrt{x^2-(m-1)x+1}}</m>', ''),
(78, 0, 10, 10, 1, 'Bất đẳng thức - bất phương trình', 'Tìm tập xác định của hàm số sau:\n<m>f(x) = \\sqrt{\\frac{3(1-x)}{15-2x-x^2} - 1}</m>', ''),
(79, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Cho phương trình <m>(m+3)x^2+(m+3)x+m=0</m>. Tìm m để:\r\na, Phương trình có một nghiệm bằng -1. Tính nghiệm còn lại.\r\nb, Phương trình có nghiệm.', ''),
(80, 0, 10, 10, 2, 'Bất đẳng thức - bất phương trình', 'Tìm giá trị nhỏ nhất của hàm số <m>y=\\frac{2x^2-x+2}{2x-1}</m> với <m>x \\in (\\tfrac{1}{2}; +\\infty)</m>', ''),
(81, 0, 10, 10, 0, 'Bất đẳng thức - bất phương trình', 'Giải hệ bất phương trình\n<m>\\left\\{ \\begin{array}{l} x^2-8x+15 \\geq 0 \\\\ x^2-12x-64 \\leq 0 \\\\ 10-2x \\geq 0 \\end{array}</m>', ''),
(82, 0, 10, 15, 2, 'Bất đẳng thức - bất phương trình', 'Cho <m>f(x) =x^2 -2(m+2)x+2m^2+10m+12 </m>.  Tìm m để:\r\na, Phương trình f(x) = 0 có 2 nghiệm trái dấu\r\nb, Bất phương trình <m>f(x) \\geq 0</m> có tập nghiệm R', ''),
(83, 0, 10, 10, 1, 'Phương trình - hệ phương trình', 'Giải phương trình <m>\\sqrt{15-x} = \\sqrt{3-x} + 2</m>', ''),
(84, 0, 10, 10, 1, 'Phương trình - hệ phương trình', 'Giải và biện luận theo tham số m phương trình <m>m^2x = m(4x+3)</m>', ''),
(85, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Trong 1 phòng họp có 360 cái ghế được xếp thành các dãy và số ghế trong mỗi dãy đều bằng nhau. Có 1 lần phòng họp phải xếp thêm 1 dãy ghế và mỗi dãy tăng 1 ghế (số ghế trong mỗi dãy bằng nhau) để đủ chỗ cho 400 đại biểu. Hỏi bình thường trong phòng có bao nhiêu dãy ghế và mỗi dãy có bao nhiêu ghế?', ''),
(86, 1, 10, 5, 0, 'Phương trình - hệ phương trình', 'Xét các khẳng định sau đây:\n1, <m>\\sqrt{x-2} = 1 \\iff x-2=1</m>\n2, <m>\\sqrt{x+2} = x \\iff x^2-x-2=0</m>\n3, <m>(\\sqrt{x})^2 = 1+2x \\iff x = 1+2x</m>\n4, <m>\\sqrt{x^2} = 1+2x \\iff x = 1+2x</m>\nSố khẳng định đúng là:\nA. 0\nB. 1\nC. 2\nD. 3', 'C. 2'),
(87, 1, 10, 5, 0, 'Phương trình - hệ phương trình', 'Cho hai phương trình\n(1)  x(x - 2) = 3(x - 2)\n(2) <m>\\frac{x(x-2)}{x-2} = 3</m>\n\nTa nói\nA. Phương trình (1) là hệ quả của phương trình (2)\nB. Phương trình (1) và (2) là hai phương trình tương đương\nC. Phương trình (2) là hệ quả của phương trình (1)\nD. Cả ba phương án A, B, C đều sai\n', 'A. Phương trình (1) là hệ quả của phương trình (2)'),
(88, 0, 10, 10, 1, 'Phương trình - hệ phương trình', 'Giải hệ phương trình\n<m>\\left\\{ \\begin{array}{l} x+3y-2x=5 \\\\ -2x-4y+5z=-17 \\\\ 3x+9y-9z=31 \\end{array}</m>', '<m>(\\frac{16}{6};\\frac{25}{3}\\frac{-16}{3}</m>'),
(89, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Giải phương trình | 2x + 3 | = x - 1', 'Phương trình vô nghiệm'),
(90, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Cho phương trình sau với m là tham số:\n<m>(2m+3)x^2 + 2(3m+2)x+m-1=0</m>\nXác định m để phương trình có một nghiệm bằng 1, sau đó tìm nghiệm còn lại.', 'm=2/3 , x=-1/7'),
(91, 0, 10, 10, 1, 'Phương trình - hệ phương trình', 'Tìm ba cạnh của tam giác vuông biết cạnh dài nhất hơn cạnh thứ thứ hai 3 đơn vị, cạnh ngắn nhất bằng 3/4 cạnh thứ hai.', ''),
(92, 1, 10, 5, 0, 'Phương trình - hệ phương trình', 'Phương trình <m>(m-1)x^2 + 6x-1=0</m> có hai nghiệm phân biệt khi\nA. m > -8\nB. m > -5/4\nC. m > -8, m &ne; 1\nD. m > -5/4, m &ne; 1', 'C. m > -8, m &ne; 1'),
(94, 0, 10, 20, 2, 'Phương trình - hệ phương trình', 'Cho phương trình <m>mx^2-2(m-2)x+m-3=0</m>\na, Giải và biện luận phương trình trên\nb, Với giá trị nào của m thì phương trình trên có hai nghiệm trái dấu\nc, Với giá trị nào của m thì phương trình trên có hai nghiệm thoả:\n<m>x_1+x_2+3x_1 x_2 =2</m>', ''),
(95, 0, 10, 15, 0, 'Phương trình - hệ phương trình', 'Giải các phương trình và hệ phương trình sau:\na, <m>\\sqrt{x^2-2x+1}=2\\sqrt{2}</m>\nb, <m>\\left\\{\\begin{array}{l}x+y+xy=5 \\\\ x^2y+xy^2=6 \\end{array}</m>', ''),
(96, 0, 10, 10, 1, 'Phương trình - hệ phương trình', 'Giải và biện luận phương trình sau:\n<m>\\frac{(2m-1)x+2}{x-2}=m+1</m>', ''),
(97, 1, 10, 5, 0, 'Phương trình - hệ phương trình', 'Phương trình <m>x^4+9x^2+8=0</m>\nA. Vô nghiệm\nB. Có ba nghiệm phân biệt\nC. Có hai nghiệm phân biệt\nD. Có bốn nghiệm phân biệt', 'A. Vô nghiệm'),
(98, 1, 10, 5, 0, 'Phương trình - hệ phương trình', 'Phương trình <m>\\sqrt{x-1} + \\sqrt{x-2} = \\sqrt{x-3}</m>\nA. Vô nghiệm\nB. Có đúng hai nghiệm\nC. Có đúng một nghiệm\nD. Có đúng ba nghiệm', 'B. Có đúng hai nghiệm'),
(99, 1, 10, 5, 0, 'Phương trình - hệ phương trình', 'Với giá trị nào của m thì phương trình <m>x^2-2mx+144=0</m> có nghiệm?\nA. m < 12 \nB. 12 &ge; m\nC. m &le; 12 hoặc m &le; -12\nD. m &le; -12 hoặc m &ge; 12', ''),
(100, 1, 10, 5, 0, 'Phương trình - hệ phương trình', 'Tìm giá trị của m để hệ phương trình sau có nghiệm duy nhất\n<m>\\left\\{ \\begin{array} mx+y=2006 \\\\ x+my=2007 \\end{array} </m>\nA. m = 1\nB. m &ne; -1\nC. m &ne; 1\nD. Đáp số khác', ''),
(101, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Tìm giá trị của tham số m để phương trình <m>2x^4-2mx^2+3m-\\tfrac{9}{2}=0</m> có 4 nghiệm phân biệt', ''),
(102, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Cho phương trình <m>mx^2-2(m+1)x+m+1=0</m> với m là tham số. Hãy tìm giá trị của m để phương trình đã cho có hai nghiệm phân biệt thoả:\na, <m>x_1 = -2x_2</m>\nb, Nghiệm này bằng 3 lần nghiệm kia.', ''),
(103, 1, 10, 5, 0, 'Phương trình - hệ phương trình', 'Hãy tìm nghiệm kép của phương trình <m>x^2-2(m+2)x+m+2=0</m> khi nó có nghiệm kép.\nA. -1\nB. 2/3\nC. 4\nD. Kết quả khác', ''),
(104, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Để chuyển 6307 quyển sách vào thư viện, nhà trường đã huy động tổng cộng 70 nam sinh của 3 lớp 10A1, 10A2, 10A3. Trong buổi lao động này, thành tích đạt được của mỗi lớp như sau:\n	Mỗi nam sinh lớp 10A1 đã chuyển được 86 quyển sách.\n	Mỗi nam sinh lớp 10A2 đã chuyển được 98 quyển sách.\n	Mỗi nam sinh lớp 10A3 đã chuyển được 87 quyển sách.\n	Cuối buổi lao động, thầy hiệu trưởng đã tuyên dương lớp 10A2 vì tuy ít hơn lớp 10A1 ba nam sinh nhưng lại chuyển được nhiều sách nhất.\n	Hỏi số nam sinh của mỗi lớp là bao nhiêu?\n', ''),
(105, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Giải và biện luận phương trình sau theo tham số m: \n<m>\\frac{x-3}{mx+2} = 3</m>', ''),
(106, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Giải phương trình sau: <m>\\sqrt{5x+2} = 3x+1</m>', ''),
(107, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Phương trình <m>\\sqrt{5x-3}-4x=4+\\sqrt{3-5x}</m> có tập nghiệm là\nA. {-1}\nB. {3/5}\nC. &empty;\nD. {-1; 3/5}', ''),
(108, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Một số tự nhiên gồm 3 chữ số. biết rằng lấy tổng các chữ số của số đó thì được 27, và nếu lấy tổng của chữ số hàng trăm  và chữ số hàng đơn vị thì được số gấp đôi chữ số hàng chục. Hơn nữa, nếu lấy hai lần chữ số hàng trăm mà trừ đi chữ số hàng chục thì được chữ số hàng đơn vị. Hãy tìm số đó. ', ''),
(109, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Giải phương trình <m>| 3x+4|-x=3</m>', ''),
(110, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Giải và biện luận phương trình <m>m^2(x-1)=mx-1</m>', ''),
(111, 1, 10, 5, 0, 'Phương trình - hệ phương trình', 'Cho hệ phương trình: \n<m>\\left\\{ \\begin{array} mx-2y=1 \\\\ 3x+2y=3 \\end{array}</m>. \nXác định m để hệ vô nghiệm.\nA. m < 3\nB. m > 3\nC. m = 3\nD. m &ne; 3\n', ''),
(112, 1, 10, 5, 0, 'Phương trình - hệ phương trình', 'Tập nghiệm của phương trình <m>\\sqrt{x^2-4}=x-2</m> là\nA. {0; 2}\nB. {0}\nC. {2}\nD. &empty;', ''),
(113, 1, 10, 5, 0, 'Phương trình - hệ phương trình', 'Điều kiện của phương trình <m>\\frac{x^2}{\\sqrt{x-2}}=\\frac{8}{\\sqrt{x-2}}</m> là:\nA. x &ne; 2\nB. x &ge; 2\nC. x < 2\nD. x > 2', ''),
(114, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Cho hàm số <m>y = x^2 - (m-3)x - m + 6</m> (m là tham số)\na, Lập bảng biến thiên và vẽ đồ thị hà số trên khi m = 5\nTìm m để phương trình <m>y = x^2 - (m-3)x - m + 6= 0</m> có hai nghiệm trái dấu', ''),
(115, 0, 10, 10, 0, 'Phương trình - hệ phương trình', 'Giải hệ phương trình \n<m>\\left\\{ \\begin{array}{l} xy+4x+4y=-23 \\\\ x^2+xy+y^2=19 \\end{array} </m>', ''),
(116, 1, 11, 5, 1, 'Tổ hợp- Xác suất', 'Tìm số hạng không chứa x trong khai triển của biểu thức:\n<m>{\\left( \\frac{1}{\\sqrt[3]{x^2}} + \\sqrt[4]{x^2} \\right)}^{17}</m> với x > 0\nA. <m>C_{17}^{8}</m>\nB. <m>C_{16}^{7}</m>\nC. <m>C_{18}^{6}</m>\nD. <m>C_{17}^{8}</m>\n', ''),
(117, 0, 10, 5, 0, 'Phương trình - hệ phương trình', 'Giải hệ phương trình:\n<m>\\left\\{ \\begin{array}{l} 3x-5y=-2 \\\\ 4x+7y=1 \\end{array}</m>.', '(9/41; -11/41)'),
(119, 0, 11, 10, 0, 'Tổ hợp- Xác suất', 'Từ các chữ số 1,2,5,6,7,8 có thể lập được bao nhiêu số gồm 3 chữ số khác nhau từ 5 chữ số trên sao cho:\na/	Số tạo thành là một số chẵn.\nb/	Số tạo thành không có mặt của chữ số 7.\n', ''),
(213, 0, 11, 10, 1, 'Tổ hợp- Xác suất', 'Từ các chữ số 1,2,5,6,7,8 có thể lập được bao nhiêu số gồm 3 chữ số khác nhau từ 5 chữ số trên sao cho:\na/	Số tạo thành phải có mặt của chữ số 1 và 5.\nb/	Số tạo thành nhỏ hơn 278.', ''),
(138, 1, 11, 5, 1, 'Đạo hàm', 'Phương trình tiếp tuyến của đồ thị hàm số <m>  y=x^3+3x^2-6  </m> tại điểm M(1; -2) là:\nA. <m> y=6x-10   </m> \nB. <m> y=2x+3   </m> \nC. <m> y=9x-7   </m> \nD. <m> y=9x-11   </m>    ', ''),
(139, 1, 11, 5, 1, 'Đạo hàm', 'Phương trình tiếp tuyến của đồ thị hàm số <m>y=x^3-2x^2+x-1    </m>  tại điểm có hoành độ <m>   x_0=-1 </m> là\nA. y=8x+11\nB. y=8x+3\nC. y=8x+8\nD. y=8x+7', ''),
(140, 1, 11, 5, 1, 'Đạo hàm', 'Hệ số góc cua tiếp tuyến với đồ thị  <m> y=2x^3-3x^2+2   </m> tại điểm có hoành độ <m> x_0=2   </m> là:\nA. 14\nB. 12\nC. 18\nD. 6', ''),
(141, 1, 11, 5, 1, 'Đạo hàm', 'Phương trình tiếp tuyến của đồ thị hàm số <m> y=f(x)=x^3   </m> tại điểm có hoành độ bằng 2 là:\nA. y=12x+6\nB. 12x-8\nC. y=12x-32\nD. y=12x-16 ', ''),
(142, 1, 11, 5, 1, 'Đạo hàm', 'Tiếp tuyến của đồ thị hàm số <m> y=f(x)=2x^3   </m> tại điểm có tung độ bằng -2 có phương trình là:\nA. y=6x+10\nB. 6x+4\nC. y=6x-1\nD. y=-6x-8', ''),
(143, 1, 11, 5, 1, 'Đạo hàm', 'Tiếp tuyến với đồ thị hàm số <m> y=\\dfrac{3x+2}{2x-3}   </m> tại điểm có hoành độ <m>x_0=1    </m> có hệ số góc bằng:\nA. -5\nB. -13\nC. 13\nD. -1', ''),
(144, 1, 11, 5, 1, 'Đạo hàm', ' Đạo hàm cấp 2 của <m> f(x)=\\dfrac{4}{5}x^5-6x^2-7x   </m>  bằng :\nA. <m> 16x^2-12   </m> \nB. <m> 4x^3-12   </m> \nC. <m>16x^3-12    </m> \nD. <m> 16x^3-12x   </m> ', ''),
(145, 1, 11, 5, 1, 'Đạo hàm', 'Đạo hàm cấp 2 của hàm số <m> y=cos 2x    </m> bằng biểu thức nào sau đây:\r\nA. <m>  -4 \\sin 2x  </m>  \r\nB. <m>  4 \\cos 2x  </m> \r\nC. <m>  -4 \\cos 2x  </m> \r\nD. <m>  -2 \\sin 2x  </m>  ', ''),
(146, 1, 11, 5, 1, 'Đạo hàm', 'Vi phân của hàm số <m>  y=2x^5-\\dfrac{2}{x}+5  </m> bằng biểu thức nào sau đây?\nA. <m> \\left(10x^4-\\dfrac{2}{x^2}\\right)dx   </m> \nB. <m> \\left(10x^4+\\dfrac{2}{x^2}+5\\right)dx   </m> \nC. <m>  \\left(10x^4+\\dfrac{2}{x^2}\\right)dx  </m> \nD. <m>  \\left(10x+\\dfrac{2}{x^2}\\right)dx  </m>  ', ''),
(147, 1, 11, 5, 1, 'Đạo hàm', 'Số gia của hàm số <m>  y=x^2+2  </m> tại điểm <m>x_0 =2   </m> ứng với số gia <m>   \\Delta x=1 </m> bằng bao nhiêu?\nA. 5\nB. 9\nC. 2\nD. 13', ''),
(148, 1, 11, 5, 1, 'Đạo hàm', 'Hàm số <m>y=\\sqrt{1-2x}    </m> có đạo hàm y'' là:\nA. <m>  \\dfrac{1}{\\sqrt{1-2x}}  </m> \nB. <m>  \\dfrac{-1}{2\\sqrt{1-2x}}  </m> \nC. <m> \\dfrac{1}{2\\sqrt{1-2x}}   </m> \nD. <m> \\dfrac{-1}{ \\sqrt{1-2x}}   </m>  ', ''),
(149, 1, 11, 5, 1, 'Đạo hàm', 'Hàm số <m> f(x)=\\dfrac{x-4}{x+5}+2\\sqrt{x}   </m> có f''(1) bằng:\nA. 2\nB.  <m>  \\dfrac{1}{2}  </m> \nC.  <m>  \\dfrac{9}{4}  </m> \n D. <m>  \\dfrac{5}{4}  </m> ', ''),
(150, 1, 11, 5, 1, 'Đạo hàm', 'Đạo hàm của hàm số <m> y=\\dfrac{1}{x^2+1}   </m> bằng biểu thức nào sau đây:\nA. <m> \\dfrac{-2x}{(x^2+1)^2}   </m> \nB. <m> \\dfrac{-x}{(x^2+1)^2}   </m> \nC. <m> \\dfrac{2x}{(x^2+1)^2}   </m> \nD. <m> \\dfrac{-2}{(x^2+1)^2}   </m>  ', ''),
(151, 1, 11, 5, 1, 'Đạo hàm', ' Đạo hàm của hàm số: <m> y=\\sqrt{3x^2-2x+12}   </m> bằng:\nA. <m> \\dfrac{6x}{2\\sqrt{3x^2-2x+12}}   </m> \nB. <m>  \\dfrac{4x}{2 \\sqrt{3x^2-2x+12}}  </m> \nC. <m>  \\dfrac{1}{2 \\sqrt{3x^2-2x+12}}   </m> \nD. <m> \\dfrac{3x-1}{ \\sqrt{3x^2-2x+12}}    </m>  ', ''),
(152, 1, 11, 5, 1, 'Đạo hàm', 'Đạo hàm của hàm số <m> y=\\cos \\left(\\dfrac{\\pi }{3}-3x\\right)   </m> bằng biểu thức nào sau đây:\nA. <m>-3 \\sin \\left(\\dfrac{\\pi }{3}-3x\\right)    </m> \nB. <m> 3 \\sin \\left(\\dfrac{\\pi }{3}-3x\\right)   </m> \nC. <m>   - \\sin \\left(\\dfrac{\\pi }{3}-3x\\right) </m> \nD. <m>    \\sin \\left(\\dfrac{\\pi }{3}-3x\\right)  </m>   ', ''),
(153, 1, 11, 5, 1, 'Đạo hàm', 'Cho <m> f(x)=\\sqrt{\\sin 2x}   </m> giá trị f'' <m>   \\left(\\dfrac{\\pi }{4}\\right) </m> bằng\nA. 1\nB. 0\nC. không xác định\nD. -1 ', ''),
(154, 1, 11, 5, 1, 'Đạo hàm', 'Cho f(x)=x^2+5x; g(x)=9x-\\dfrac{3}{2}x^2. Giá trị x để f''(x)=g''(x) là:\nA. <m>  \\dfrac{5}{4}  </m> \nB. -4\nC. <m>  \\dfrac{4}{5}  </m>  \nD. 4', ''),
(155, 1, 11, 5, 1, 'Đạo hàm', 'Cho <m>f(x)=x^3+2x^2-7x+3    </m> . Tập hợp các giá trị của x để f''(x) <m>\\leq 0    </m> là:\nA. <m> \\left[-\\dfrac{7}{3}; 1\\right]   </m> \nB. <m>  \\left[1; -\\dfrac{7}{3} \\right]    </m> \nC. <m>  \\left(-\\dfrac{7}{3}; 1\\right)  </m> \nD. <m> \\left\\{-\\dfrac{7}{3}; 1\\right\\}      </m>  ', ''),
(156, 1, 11, 5, 1, 'Đạo hàm', 'Tiếp tuyễn của đồ thị hàm số <m>y=f(x)=x^4    </m> mà hệ số góc của tiếp tuyến bằng 4 có phương trình là :\nA. y=4x-4\nB. y=4x-3\nC. y=4x+5\nD. y=4x\n\n', ''),
(157, 1, 11, 5, 1, 'Đạo hàm', 'Phương trình tiếp tuyến của đồ thị hàm số <m>  y=\\dfrac{x^2+1}{x-1}  </m> tại điểm <m>  M(2; 5)  </m> là:\nA. y=2x-3\nB. y=x+3\nC. y=-x+7\nD. y=2x+7', ''),
(158, 1, 11, 5, 1, 'Đạo hàm', 'Đạo hàm cấp hai của hàm số <m> f(x)=\\dfrac{4}{5}x^5-3x^2-x+4   </m>  bằng biểu thức nào sau đây:\nA. <m>4x^2-6x    </m> \nB. <m>4x^3-6    </m> \nC. <m>16x^3-6x    </m> \nD. <m> 16x^3-6   </m>  ', ''),
(159, 1, 11, 5, 0, 'Đạo hàm', 'Công thức đạo hàm nào đúng \nA. (u.v)''=u''.v+u.v''\nB. (u.v)''=u''.v''\nC. (u.v)''= u.v''\nD. (u.v)''=u''+v''', ''),
(160, 0, 11, 10, 1, 'Tổ hợp- Xác suất', 'Giải phương trình sau:\r\n<m>  2A_x^2+50=A_{2x}^2 </m> \r\n', ''),
(288, 0, 11, 10, 1, 'Tổ hợp- Xác suất', 'Giải phương trình sau:\n <m> {frac{1}{C^x_4}-\\dfrac{1}{C_5^x}=\\dfrac{1}{C_6^x} <m/>', ''),
(161, 0, 11, 15, 1, 'Tổ hợp- Xác suất', 'Giải các phương trình sau:\r\na/ <m>  2A_x^2+50=A_{2x}^2 </m> \r\nb/ <m> \\frac{1}{C^x_4}-\\frac{1}{C_5^x}=\\frac{1}{C_6^x} </m>', ''),
(162, 0, 11, 10, 1, 'Tổ hợp- Xác suất', 'Tìm k sao cho các số  <m> C_k^7;  C^{k+1}_7;  C^{k+2}_7 </m> theo thứ tự lập thành một cấp số cộng.', ''),
(163, 0, 11, 10, 1, 'Tổ hợp- Xác suất', 'Giải phương trình sau:\r\n<m> C^4_{n-1}-C^3_{n-1}-\\frac{5}{4}A^2_{n-2}<0, n \\in \\mathbb{N} </m>.\r\n', ''),
(289, 0, 11, 10, 1, 'Tổ hợp- Xác suất', 'Giải bất phương trình sau:\n<m> A^3_n+2C^{n-2}_n \\leq 9n </m>.', ''),
(164, 0, 11, 10, 0, 'Tổ hợp- Xác suất', 'Giải hệ phương trình sau:\n<m> C^y_{x+1}:C^{y+1}_x:C^{y-1}_x=6:5:2 </m>.', ''),
(165, 0, 11, 15, 1, 'Tổ hợp- Xác suất', 'Giải các phương trình sau:\na/ <m> P_xA^2_x-72=6(A^2_x-2P_x) </m>\nb/ <m> \\frac{1}{C^x_5}-\\dfrac{1}{C^x_6}=\\frac{14}{C^x_7} </m>', ''),
(166, 0, 11, 15, 1, 'Tổ hợp- Xác suất', 'Giải các phương trình sau:\na/ <m> C^2_{n+1}+2C^2_{n+2}+2C^2_{n+3}+C^2_{n+4}=149 </m>\nb/ <m> C^1_x+6C^2_x+6C^3_x=9x^2-14x </m>', ''),
(167, 0, 11, 20, 1, 'Tổ hợp- Xác suất', 'Giải các bất phương trình sau:\na/ <m> \\frac{C^{x-3}_{x-1}}{A^4_{x+1}}<\\frac{1}{14P} </m>\nb/ <m> C^4_{x-1}-C^3_{x-1}-\\dfrac{5}{4}A^2_{x-2}<0 </m>', ''),
(168, 0, 11, 20, 2, 'Tổ hợp- Xác suất', 'Giải các phương trình sau:\na/ <m> \\dfrac{1}{2}A^2_{2x}-A^2_x \\leq \\dfrac{6}{x}C^3_x+10 </m>\nb/ <m> C^2_{2x}+C^4_{2x}+ ... +C^{2x}_{2x}\\geq 2^{2003}-1 </m>', ''),
(169, 0, 11, 20, 2, 'Tổ hợp- Xác suất', 'Giải hệ phương trình sau:\n<m> C^{m+1}_{n+1}:C^m_{n+1}:C^{m-1}_{n+1}=5:5:3 </m>', ''),
(170, 0, 11, 15, 2, 'Tổ hợp- Xác suất', 'Giải bất phương trình <m> \\dfrac{P_{n+5}}{(n-k)!}\\leq 60A^{k+2}_{n+3} </m>. Với hai ẩn n, k thuộc N.', ''),
(171, 0, 11, 15, 2, 'Tổ hợp- Xác suất', 'Giải hệ phương trình: <m> C^y_{x+1}:C^{y+1}_x:C^{y-1}_x=6:5:2 </m>', ''),
(172, 0, 11, 15, 2, 'Tổ hợp- Xác suất', 'Giải bất phương trình <m> C^2_{2x}+C^4_{2x}+ ... +C^{2x}_{2x}\\geq 2^{2003}-1 </m>', ''),
(173, 0, 11, 15, 2, 'Tổ hợp- Xác suất', 'Tìm số n nguyên dương thỏa mãn bất phương trình <m> A^3_n+2.C^{n-2}_n \\leq 9n </m>', ''),
(174, 0, 11, 10, 1, 'Tổ hợp- Xác suất', 'Tìm số tự nhiên n thỏa mãn <m> C^2_n.C^{n-2}_n+2C^2_n.C^3_n+C^3_n.C^{n-3}_n=100 </m>\n', ''),
(175, 0, 11, 10, 0, 'Tổ hợp- Xác suất', 'Biết rằng <m> (2+x)^{100}=a_0+a_1x+ ... +a_{100}x^{100} </m>\na/ CMR <m> a_2 </m>\nb/ Với giá trị nào của k thì <m>a_k </m>', ''),
(176, 0, 11, 10, 1, 'Tổ hợp- Xác suất', 'Tìm số nguyên n >1 thỏa mãn đẳng thức <m> 2P_n+6A^2_n-p_nA^2_n=12 </m> ', ''),
(177, 0, 11, 10, 1, 'Tổ hợp- Xác suất', 'Tính giá trị của biểu thức <m> M=\\dfrac{A^4_{n+1}+3A^3_n}{(n+1)!} n </m> là số nguyên dương. Biết rằng <m> C^2_{n+1}+2C^2_{n+2}+2C^2_{n+3}+C^2_{n+4}=149 </m>', ''),
(178, 0, 11, 5, 1, 'Tổ hợp- Xác suất', 'Tìm hệ số của <m> x^7 </m> trong khai triển thành đa thức của <m> (2-3x)^{20} </m>', ''),
(179, 0, 11, 10, 2, 'Tổ hợp- Xác suất', 'Giả sử <m> (1+2x)^n=a_0+a_1x+ ... +a_nx^n </m> và <m> a_0+a_1+ ... +a_n=729 </m>\nTìm n và số lớn nhất trong các số <m> a_0, a_1, ..., a_n </m>', ''),
(180, 0, 11, 10, 2, 'Tổ hợp- Xác suất', 'Giả sử n là số nguyên dương và <m> (1+x)^n=a_0+a_1x+ ... +a_nx^n </m>. Biết rằng k nguyên dương sao cho <m> \\dfrac{a_{k-1}}{2}=\\dfrac{a_k}{9}=\\dfrac{a_{k+1}}{24} </m>. Tìm n ?', ''),
(181, 0, 11, 10, 2, 'Tổ hợp- Xác suất', 'Giả sử n là số nguyên dương và <m> (1+x)^{10}(x+2)=x^{11}+a_1+a_1x^{10}+ ... a_{11} </m>. Hãy tính hệ số <m> a_5 </m> ', ''),
(182, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, hãy tìm tọa độ các đỉnh B, C của tam giác ABC biết A(- 1; - 3), trọng tâm G(4;-2) đường thẳng trung trực AB có phương trình 3x + 2y -4 = 0.', ''),
(183, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho hai đường thẳng d 2x - y + 5 = 0 , d'': 3x + 6y - 7 = 0. Lập phương trình đường thẳng đi qua p(2;- 1) sao cho đường thẳng đó cắt d, d'' tạo ra một tam giác cân có đỉnh là giao điểm của d và d''.', ''),
(184, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho hai điểm A (1; 0), B(3, -1) và đường thẳng d: x - 2y -1 = 0. Tìm điểm C thuộc d sao cho diện tích tam giác ABC bằng 6 ( đvdt).', ''),
(185, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho tam giác ABC có A thuộc đường thẳng d: x - 4y -2 = 0, BC song song với d . PT đường cao BH: x + y + 3 = 0 và trung điểm  AC là M (1;1). Tìm tọa độ các đỉnh của tam giác ABC.', ''),
(186, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho tam giác ABC có A thuộc đường thẳng d: x - 4y -2 = 0, BC song song với d . PT đường cao BH: x + y + 3 = 0 và trung điểm  AC là M (1;1). Tìm tọa độ các đỉnh của tam giác ABC.', ''),
(188, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho hai điểm A (2;-1), B(1;-2),  trọng tâm G thuộc đường thẳng d: x + y - 2 = 0. Tìm tọa độ điểm c biết diện tích tam giác ABC bằng 3/2.', ''),
(189, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho tam giác ABC  với A (1;5), B(-4;-5), C(4;-1). Tìm tọa độ trực tâm  và tâm đường tròn ngoại tiếp tam giác ABC.', ''),
(190, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng Oxy, cho tam giác ABC  vuông tại C, biết A(-2;0), B(2;0) và khoảng cách  từ trọng tâm G đến trục hoành bằng 1/3. Tìm tọa độ đỉnh C.', ''),
(191, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho đường thẳng d: 3x - 4y + 1 = 0. Lập phương trình đường thẳng song song với d và cách d một khoảng bằng 1.', ''),
(192, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho tam giác ABC có A (2;2). Lập phương trình các cạnh của tam giác  biết phương trình đường cao kẻ từ B và C tương ứng là : 9x-3y - 4 = 0 và x + y - 2 = 0.', ''),
(193, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho tam giác ABC có đỉnh A(-1;2) có phương trình trung tuyến CM : 5x + 7y - 20 = 0 và đường cao BK : 5x - 2y -4 = 0. Viết phương trình các cạnh AC và BC.', ''),
(194, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, viết phương trình các cạnh của tam giác ABC biết C(4;3), đường phân giác trong và trung tuyến kẻ từ một đỉnh của tam giác có phương trình lần lượt là: \nx + 2y - 5= 0 và 4x + 2y -10 =0.', ''),
(120, 0, 11, 5, 0, 'Tổ hợp- Xác suất', 'Giải phương trình : <m>$    2A_x^2+50=A_{2x}^2   $</m> \n\n', ''),
(215, 0, 10, 15, 1, 'Bất đẳng thức - bất phương trình', 'Giải các bất phương trình sau:\na,<m> \\frac{2x-4}{\\sqrt{x^2-3x-10}} > 1</m>\nb, <m>\\sqrt{x^2+6x+8} \\leq 2x+3</m>', ''),
(218, 0, 12, 5, 0, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m> \\int^{0}_{2}{(\\sqrt{3x+1}dx}</m>', ''),
(219, 0, 12, 5, 1, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m> \\int^{0}_{2}{(\\sqrt{x^2+4})dx}</m>', ''),
(220, 0, 12, 5, 1, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m> \\int^{1}_{2}{\\frac{3x+1}{2x-1}}dx}</m>', ''),
(221, 0, 12, 5, 1, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m> \\int^{0}_{1}{\\frac{3x^3-1}{2x+1}}dx}</m>', ''),
(222, 0, 12, 10, 1, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m> \\int^{-1}_{2}{\\frac{xdx}{x+2}}</m>', ''),
(223, 0, 12, 10, 1, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m> \\int^{\\frac{\\pi}{2}}_{0}{sin^5xdx}</m>', ''),
(224, 0, 12, 10, 1, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m> \\int^{\\frac{\\pi}{3}}_{\\frac{\\pi}{4}}{\\frac{1}{sin2x}dx}</m>', ''),
(121, 0, 12, 15, 0, 'Hình học không gian 12', 'Cho hình lăng trụ đứng ABC.A''B''C'' có đáy là một tam giác vuông tại A, AC=a, <m>{\\widehat{C}={60^{0}}</m>. Đường chéo BC'' của mặt bên (BB''C''C) tạo với mp(AA''C''C) một góc <m>{60^{0}}</m>.\na. Tính độ dài đoạn AC''.\nb. Tính thể tích khối lăng trụ.', ''),
(122, 0, 11, 10, 2, 'Tổ hợp- Xác suất', 'Tính giá trị của biểu thức <m>$    M=\\dfrac{A^4_{n+1}+3A^3_n}{(n+1)!} n   $</m> là số nguyên dương. Biết rằng <m>$    C^2_{n+1}+2C^2_{n+2}+2C^2_{n+3}+C^2_{n+4}=149   $</m> \r\n', ''),
(123, 0, 10, 10, 0, 'Phương pháp tọa độ trong mặt phẳng', 'Cho tam giác cân ABC, AB = BA, với A(1;-1), C(3;5), đỉnh B nằm trên đường thẳng (d): 2x-y=0. Viết phương trình các đường thẳng AB và AC.', ''),
(124, 0, 11, 10, 0, 'Tổ hợp- Xác suất', 'Tìm hệ số của số hạng chứa <m>    x^8   </m> trong khai triển nhị thức. Biết <m> C^{n+1}_{n+4}-C^n_{n+3}=7(n+3) </m>', ''),
(197, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho tam giác ABC có B(3;5),C(4;-3). Đường phân giác trong của góc A: x + 2y - 8 = 0. Tính diện tích của tam giác ABC.', ''),
(198, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho tam giác ABC có B(-4;0), đường cao kẻ từ đỉnh A có phương trình : -4x + y + 3 = 0. Viết phương trình ba cạnh của tam giác .', ''),
(125, 0, 11, 10, 2, 'Tổ hợp- Xác suất', 'Cho khai triển <m>$    \\left(2^{\\frac{x-1}{2}}+2^{\\frac{-x}{3}}\\right)^n.  $</m>  Biết <m>$    C^3_n =5C^1_n   $</m> và số hạng thứ 4 bằng 20n. Tìm x và n.', ''),
(126, 1, 11, 10, 0, 'Tổ hợp- Xác suất', 'Trong khai triển <m>$    \\left(\\sqrt[3]{\\dfrac{a}{\\sqrt{b}}}+\\sqrt{\\dfrac{b}{\\sqrt[3]{a}}}\\right)^n.   $</m> Tìm số hạng chứa a, b có số mũ bằng nhau.\r\n', ''),
(286, 0, 11, 10, 2, 'Tổ hợp- Xác suất', 'Tìm hệ số lớn nhất trong các hệ số của khai triển <m>$    \\left(\\dfrac{1}{3}+\\dfrac{2}{3}x\\right)^{40}  $</m> \n', ''),
(287, 0, 11, 10, 2, 'Tổ hợp- Xác suất', 'Biết tổng các hệ số trong khai triển <m>$    (1+2x)^n   $</m> bằng 6561. Tìm hệ số của <m>$    x^4   $</m> ', ''),
(127, 0, 11, 10, 2, 'Tổ hợp- Xác suất', 'Chứng minh rằng: <m>  C^1_n+2C^2_n+3C^3_n+ ... +nC^n_n=n2^{n-1}, \\forall  n \\in \\mathbb{N}, n \\geq 1   </m> ', ''),
(128, 0, 11, 10, 0, 'Tổ hợp- Xác suất', 'Chứng minh rằng <m>   1.2^{n-1}C^1_n+2.2^{n-2}.C^2_n+3.2^{n-3}C^3_n+ ... +nC^n_n=n3^{n-1}, \\forall  n \\in \\mathbb{N}, n \\geq 1  </m> ', ''),
(129, 0, 11, 10, 0, 'Tổ hợp- Xác suất', 'Chứng minh rằng <m>  \\dfrac{1}{2}C^0_n-\\dfrac{1}{4}C^1_n+\\dfrac{1}{6}C^2_n+ ...+ (-1)^n\\dfrac{1}{2n+2}C^n_n=\\dfrac{1}{2n+2} </m> ', ''),
(130, 1, 11, 10, 0, 'Tổ hợp- Xác suất', 'Từ các chữ số 0, 1, 2, 3, 4, 5,6. Có thể lập được bao nhiêu số tự nhiên có 5 chữ số khác nhau?\r\nA. 1260\r\nB. <m>\\4A^{4}_{6}-3A^{3}_{5}</m>.\r\nC.<m>\\((A^{5}_{7}-A^{4}_{6})- (3A^{4}_{6}-3A^{3}_{5})</m>.\r\nD. <m>\\Tất cả A, B, C đều đúng.', ''),
(131, 0, 12, 20, 0, 'Khảo sát hàm số', 'Cho hàm số y = <m>\\frac{x+2}{3x-1}</m> \r\n1. Khảo sát sự biến thiên và vẽ đồ thị hàm số đã cho. \r\n2. Chứng minh rằng với mọi m đường thẳng y = 2x + m luôn cắt đồ thị tại hai điểm phân biệt A và B. ', ''),
(132, 0, 12, 20, 0, 'Khảo sát hàm số', 'Cho hàm số y = <m>\\frac{3x-2}{x+1}</m> có đồ thị (C).\n1. Khảo sát sự biến thiên và vẽ đồ thị (C). \n2. Viết phương trình tiếp tuyến của đồ thị (C) tại điểm có tung độ bằng -2.', ''),
(196, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho tam giác ABC, A(1;3), B(0;1), C(-4;-1)\na) Tìm tọa độ chân H của đường cao kẻ từ đỉnh A.\nb) Tính diện tích, chu vi của tam giác ABC.', ''),
(195, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho tam giác ABC có đường phân giác trong AD: x - y = 0, đường cao CH: 2x+ y + 3 = 0, cạnh AC qua M(0;-1), AB = 2AM. Viết phương trình các cạnh của tam giác ABC.', ''),
(199, 0, 10, 10, 1, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho hình chữ nhật ABCD có điểm I (6;2) là giao điểm của hai đường chéo AC và BD. Điểm M (1;5) thuộc đường thẳng AB và trung điểm E của cạnh CD thuộc đường thẳng d: x + y - 5 = 0. Viết phương trình đường thẳng AB.', ''),
(200, 0, 10, 15, 2, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho hình chữ nhật ABC có tâm I(1/2;0), phương trình đường thẳng AB là x -2y + 2 = 0 và AB =2AD. Tìm tọa đọ các đỉnh A, B, C, D biết rằng đỉnh A có hoành độ âm.', ''),
(201, 0, 10, 15, 2, 'Phương pháp tọa độ trong mặt phẳng', 'Trong mặt phẳng tọa độ Oxy, cho tam giác ABC có đỉnh A (3;-7), trực tâm H(3;-1), tâm đường tròn ngoại tiếp là I(-2;0). Xác định tọa độ đỉnh C, biết C có hoành độ dương.', ''),
(202, 0, 11, 15, 1, 'Phương trình lượng giác', 'Giải các phương trình sau:\r\na/    <m>   \\sin 2x=\\sin \\dfrac{\\pi }{6}</m>\r\nb/ <m> \\sqrt{3}\\sin x+\\cos x=2 \\cos \\left(-\\dfrac{\\pi }{4}\\right) </m>', ''),
(290, 0, 11, 15, 0, 'Hình học không gian 11', 'Cho bốn điểm A,B,C,D không đồng phẳng\r\na) Chứng minh ba trong bốn điểm này không thẳng hàng.\r\nb) Chứng minh AC chéo với BD?', '(dùng chứng minh phản chứng).'),
(291, 0, 11, 15, 0, 'Hình học không gian 11', 'Cho hai đường thẳng chéo nhau a và b. Trên a lấy hai điểm A, B; Trên b lấy hai điểm C, D\na) Chứng minh AC chéo với BD?\nb) Lấy M nằm trên đoạn AC; N nằm trên BD. Đường thẳng MN có song song AB hoặc CD không?\nc) O là trung điểm MN. Chứng minh A, O, C, N đồng phẳng.', 'a) Dùng chứng minh phản chứng.\r\nb) Dùng chứng minh phản chứng.\r\nc) Do A, C, M, N đồng phẳng, mà O thuộc MN nên O thuộc (ACN).'),
(203, 0, 11, 5, 0, 'Phương trình lượng giác', 'Giải phương trình:\na/ <m> \\cos \\left(\\dfrac{\\pi }{2}-x\\right)=\\sin \\dfrac{7 \\pi }{4}</m>\nb/ <m> \\sqrt{2}\\sin x-\\sqrt{2}\\cos x=2\\sin (\\pi -x)</m>\n', ''),
(204, 0, 11, 5, 0, 'Phương trình lượng giác', 'Giải phương trình:\na/  <m> \\sin 3x=\\sin \\dfrac{\\pi }{4}</m>\nb/ <m> -\\sqrt{3}\\sin x+\\cos x=2 \\sin \\left(-\\dfrac{2 \\pi }{3}\\right)</m>\n', '');
INSERT INTO `question` (`id`, `type`, `grade`, `estimate`, `difficulty`, `category`, `content`, `answer`) VALUES
(205, 0, 11, 15, 0, 'Phương trình lượng giác', 'Giải phương trình:\r\na/ <m> \\sin 4x=\\sin \\dfrac{\\pi }{3}</m>\r\nb/ <m> \\sqrt{3} \\sin x- \\cos x=2 \\sin  \\left( \\dfrac{-5 \\pi }{6}\\right)</m>\r\n', ''),
(206, 0, 11, 15, 0, 'Phương trình lượng giác', 'Giải phương trình:\r\na/ <m> \\sin 4x=\\sin \\dfrac{2\\pi }{3}</m>\r\nb/ <m> \\sqrt{3} \\sin x+ \\cos x=-2 \\cos \\left( -\\dfrac{\\pi }{4}\\right)</m>\r\n', ''),
(207, 0, 11, 10, 0, 'Phương trình lượng giác', 'Giải phương trình:\na/  <m> \\cos 6x=\\sin \\left(\\dfrac{\\pi }{2}-2x\\right) </m>\nb/ <m> -\\sqrt{2}\\cos x +\\sqrt{2}\\sin x=\\cot \\dfrac{5\\pi }{2}</m>\n', ''),
(208, 0, 11, 5, 0, 'Phương trình lượng giác', 'Giải phương trình:\na/ <m> \\sin (x+2)=\\dfrac{1}{2}</m> \nb/ <m>\\tan \\left(\\dfrac{\\pi }{6}-2x\\right)=\\sqrt{3}</m>\n', ''),
(209, 0, 11, 15, 0, 'Phương trình lượng giác', 'Giải phương trình:\r\na/ <m> \\sqrt{3}\\sin x+\\cos x=2 \\sin \\dfrac{13 \\pi }{6}</m> \r\nb/ <m> 4 \\sin ^2x-3=0 </m>\r\n', ''),
(211, 0, 10, 5, 0, 'Phương pháp tọa độ trong mặt phẳng', 'Cho vectơ <m> \\vec{ a }=(3;-4), \\vec{ b }=(-1;2)</m> .Tìm tọa độ của vec tơ <m> \\vec{ a }+\\vec{ b } </m> .', ''),
(212, 0, 10, 5, 0, 'phương pháp tọa độ trong mặt phẳng', 'Cho <m> \\vec{ a }=(-5;0), \\vec{ b }=(4;x)</m>.  Tìm x để hai vec tơ  <m> \\vec{ a }, \\vec{ b } </m> cùng phương.  \n', ''),
(225, 0, 12, 10, 1, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m> \\int^{\\frac{\\pi}{2}}_{0}{\\frac{1}{cosx}dx}</m>', ''),
(226, 0, 12, 10, 1, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m> \\int^{\\frac{\\pi}{2}}_{0}{(2x+1)cosxdx}</m>', ''),
(227, 0, 12, 10, 1, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m> \\int^{\\pi}_{0}{(2x^2-1)sinxdx}</m>', ''),
(228, 0, 12, 10, 1, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m> \\int^{\\frac{\\pi}{12}}_{0}{\\frac{1}{cos^23x(1+tan3x)}dx}</m>', ''),
(229, 0, 12, 10, 1, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m>\\int^{2}_{1}{x\\sqrt{x^2+3}dx}</m>', ''),
