(233, 0, 12, 5, 0, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m>\\int^{4}_{1}{(2\\sqrt{x}-3)^2}dx}</m>', ''),
(234, 0, 12, 5, 0, 'Nguyên hàm- Tích phân', 'Tính tích phân:\n<m>\\int^{1}_{0}{(e^{2x}+\\frac{3}{2x-5})}dx}</m>', ''),
